import {React, Component, useEffect, useState} from 'react';
import L from 'leaflet';
import boundary from './geojson/population.geojson';
import {
    BrowserRouter as Router,
    Routes,
    Route,
} from "react-router-dom";
import {Button, Dropdown, Table} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';

delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('./images/marker-icon-2x.png'),
    iconUrl: require('./images/marker-icon.png'),
    shadowUrl: require('./images/marker-shadow.png')
});


class App extends Component {
    render () {
        return (
            <Router>
                <div>
                    {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
                    <Routes>
                        <Route path="/" element={<Start />}/>
                        <Route path="/city-picker" element={<CityPicker />}/>
                        <Route path="/link-picker" element={<LinkPicker />}/>
                        <Route path="/job-management" element={<JobManagerPicker />}/>
                    </Routes>
                </div>
            </Router>
        )
        // return <div id={"map"}></div>;
    }

}
export default App;

function Start() {
    return (
        <div>
            <h2>What would you like to do?</h2>
            <Button href={"/city-picker"}>Create new model</Button>{' '}
            <Button href={"/job-management"}>Pick model from database</Button>
        </div>
    );
}

function Map() {
    useEffect(() => {
        console.log(boundary)
        let map = L.map('map', {
            center: [50.07501157760184, 14.416865286199549],
            zoom: 13,
            layers: [
                L.tileLayer('https://stamen-tiles.a.ssl.fastly.net/{id}/{z}/{x}/{y}.png', {
                    attribution: 'Map tiles by <a href="http://stamen.com">Stamen Design</a>, under <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a>. Data by <a href="http://openstreetmap.org">OpenStreetMap</a>, under <a href="http://www.openstreetmap.org/copyright">ODbL</a>.',
                    maxZoom: 15,
                    id: 'toner-lite',
                    tileSize: 512,
                    zoomOffset: -1
                }),
            ],
            preferCanvas: true
        });

        let myStyle = {
            radius : 3,
            fillColor : "rgba(243,225,5,0.76)",
            color : "rgba(243,225,5,0.76)",
            weight : 1,
            opacity : 0.1,
            fillOpacity : 0.8
        };

        L.geoJSON(boundary, {
            pointToLayer: function (feature, latlng) {
                return L.circleMarker(latlng, myStyle);
            }
        }).addTo(map);
        console.log(map);
    });

    return <div id={"map"}></div>;
}

function CityPicker() {
    return (
        <div>
            <h2>Residential Clustering</h2>
            <Dropdown>
                <Dropdown.Toggle id="dropdown-basic">
                    Pick a city
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item href="/link-picker">Prague</Dropdown.Item>
                    <Dropdown.Item href="/link-picker">Bratislava</Dropdown.Item>
                    <Dropdown.Item href="/link-picker">Warsaw</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
}

function LinkPicker() {
    return <h2>Link picker</h2>;
}

function JobManagerPicker() {
    let [name, setName] = useState(null);
    let [distWalk, setDistWalk] = useState(null);
    let [distDrive, setDistDrive] = useState(null);
    let [city, setCity] = useState(null);

    function handleClick(name, distWalk, distDrive, city){
        setName(name);
        setDistWalk(distWalk);
