import {React, useEffect, useState} from "react";
import {Accordion, AccordionDetails, AccordionSummary, Checkbox, Typography} from "@mui/material";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import './LinkPicker.css'
import routes from "../data/links.json"
import L from "leaflet";

export function LinkPicker() {
    let routeTypesGrouped = {};
    Object.keys(routes).map((t) => routeTypesGrouped[t] = Object.keys(routes[t]));

    // let map = L.map('map', {
    //     center: [50.07501157760184, 14.416865286199549],
    //     zoom: 13,
    //     layers: [
    //         L.tileLayer('https://stamen-tiles.a.ssl.fastly.net/{id}/{z}/{x}/{y}.png', {
    //             attribution: 'Map tiles by <a href="http://stamen.com">Stamen Design</a>, under <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a>. Data by <a href="http://openstreetmap.org">OpenStreetMap</a>, under <a href="http://www.openstreetmap.org/copyright">ODbL</a>.',
    //             maxZoom: 18,
    //             id: 'toner-lite',
    //             tileSize: 512,
    //             zoomOffset: -1
    //         }),
    //     ],
    //     preferCanvas: true
    // });
    //
    // let myStyle = {
    //     radius : 3,
    //     fillColor : "rgba(243,225,5,0.76)",
    //     color : "rgba(243,225,5,0.76)",
    //     weight : 1,
    //     opacity : 0.1,
    //     fillOpacity : 0.8
    // };

    let [isCheckAll, setIsCheckAll] = useState({
        "tram": false,
        "metro": false,
        "bus": false,
        "funicular": false
    });

    let [checkBool, setCheckBool] = useState(false);

    let [isCheck, setIsCheck] = useState({
        "tram": [],
        "metro": [],
        "bus": [],
        "funicular": []
    });

    useEffect(() => {
    }, [checkBool]);

    function handleSelectAllClick(routeTypeName) {
        isCheckAll[routeTypeName] = !isCheckAll[routeTypeName];
        setCheckBool(!checkBool);

        if (isCheckAll[routeTypeName]) {
            isCheck[routeTypeName] = routeTypesGrouped[routeTypeName];
        } else {
            isCheck[routeTypeName] = [];
        }
    }

    function handleClick(routeName, routeType){
        setCheckBool(!checkBool);
        if (!isCheck[routeType].includes(routeName)) {
            isCheck[routeType].push(routeName);
        } else {
            isCheck[routeType] = isCheck[routeType].filter(route => route !== routeName);
            isCheckAll[routeType] = false;
        }
    }

    return (
        <div class="mainDiv">
            <h2 class="header">Route picker</h2>
            <div class="divWithAccordions">
                {
                    Object.keys(routeTypesGrouped).map((routeTypeName) => (
                        <Accordion class="accordion" style={{ backgroundColor: "lightgray"}}>
                            <AccordionSummary
                                expandIcon={<ExpandMoreIcon />}
                                aria-controls="panel1a-content"
                                id="panel1a-header"
                            >
                                <Typography class="accordionName">{routeTypeName}</Typography>
                            </AccordionSummary>
                            <AccordionDetails class="accordionDetails">
                                <div><Checkbox style={{ padding: 0}} onClick={() => handleSelectAllClick(routeTypeName)} checked={isCheckAll[routeTypeName]}/> Select All</div>
                                <div class="rowCheckboxes">
                                    {
                                        Object.values(routeTypesGrouped[routeTypeName]).map((route) => (
                                            <div className="columnCheckboxes">{route} <Checkbox onChange={() => handleClick(route, routeTypeName)} checked={isCheck[routeTypeName].includes(route)} /></div>
                                        ))
                                    }
                                </div>
                            </AccordionDetails>
                        </Accordion>
                    ))
                }
            </div>
            {/*<div class="column">*/}
            {/*    <div id={"map"}></div>;*/}
            {/*</div>;*/}
            {/*<div class="column">*/}
            {/*    /!* TODO: map here *!/*/}
            {/*</div>*/}
        </div>
    );


}
