import {React, useEffect, useState} from "react";
import {Accordion, AccordionDetails, AccordionSummary, Checkbox, Typography} from "@mui/material";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import './LinkPicker.css'
import routes from "../data/links.json"
import L from "leaflet";

export function LinkPicker() {
    let routeTypesGrouped = {};
    Object.keys(routes).map((t) => routeTypesGrouped[t] = Object.keys(routes[t]));

    const [isCheckAll, setIsCheckAll] = useState({
        "tram": false,
        "metro": false,
        "bus": false,
        "funicular": false
    });

    const [checkBool, setCheckBool] = useState(false);

    const [isCheck, setIsCheck] = useState({
        "tram": [],
        "metro": [],
        "bus": [],
        "funicular": []
    });

    console.log(isCheckAll);

    useEffect(() => {
        let map = L.map('map', {
            center: [50.07501157760184, 14.416865286199549],
            zoom: 13,
            layers: [
                L.tileLayer('https://stamen-tiles.a.ssl.fastly.net/{id}/{z}/{x}/{y}.png', {
                    attribution: 'Map tiles by <a href="http://stamen.com">Stamen Design</a>, under <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a>. Data by <a href="http://openstreetmap.org">OpenStreetMap</a>, under <a href="http://www.openstreetmap.org/copyright">ODbL</a>.',
                    maxZoom: 18,
                    id: 'toner-lite',
                    tileSize: 512,
                    zoomOffset: -1
                }),
            ],
            preferCanvas: true
        });
    }, [])

    function handleSelectAllClick(routeTypeName) {
        let newIsCheckAll = { ...isCheckAll };
        console.log(newIsCheckAll);
        newIsCheckAll[routeTypeName] = !newIsCheckAll[routeTypeName];
        setIsCheckAll(newIsCheckAll);

        let newIsCheck = { ...isCheck };

        if (newIsCheckAll[routeTypeName]) {
            console.log(routeTypesGrouped[routeTypeName]);
            newIsCheck[routeTypeName] = routeTypesGrouped[routeTypeName];
        } else {
            newIsCheck[routeTypeName] = [];
        }
        setIsCheck(newIsCheck);

        console.log(isCheckAll);
    }

    function handleClick(routeName, routeType){
        // setCheckBool(!checkBool);
        let newIsCheck = { ...isCheck };
        if (!isCheck[routeType].includes(routeName)) {
            // isCheck[routeType].push(routeName);
            newIsCheck[routeType] = [...newIsCheck[routeType], routeName];
        } else {
            // isCheck[routeType] = isCheck[routeType].filter(route => route !== routeName);
            newIsCheck[routeType] = [...newIsCheck[routeType].filter(route => route !== routeName)];
            // isCheckAll[routeType] = false;
            let newIsCheckAll = { ...isCheckAll };
            newIsCheckAll[routeType] = false;
            setIsCheckAll(newIsCheckAll);
        }
        setIsCheck(newIsCheck);
    }

    return (
        <div className="mainDiv">
            <h2 className="header">Route picker</h2>
            <div className="divWithAccordions">
                {
                    Object.keys(routeTypesGrouped).map((routeTypeName) => (
                        <Accordion className="accordion" key={ routeTypeName }>
                            <AccordionSummary
                                expandIcon={<ExpandMoreIcon />}
                                aria-controls="panel1a-content"
                                id="panel1a-header"
                            >
                                <Typography className="accordionName">{routeTypeName}</Typography>
                            </AccordionSummary>
                            <AccordionDetails className="accordionDetails">
                                <div><Checkbox style={{ padding: 0}} onClick={() => handleSelectAllClick(routeTypeName)} checked={isCheckAll[routeTypeName]}/> { !isCheckAll[routeTypeName] ? 'Select All' : 'Unselect All' }</div>
                                <div className="rowCheckboxes">
                                    {
                                        Object.values(routeTypesGrouped[routeTypeName]).map((route) => (
                                            <div className="columnCheckboxes" key={ routeTypeName + ' ' + route }>
                                                {route} <Checkbox onChange={() => handleClick(route, routeTypeName)} checked={isCheck[routeTypeName].includes(route)} />
                                            </div>
                                        ))
                                    }
                                </div>
                            </AccordionDetails>
                        </Accordion>
                    ))
                }
            </div>
            <div class="column">
                <div id={"map"}></div>
            </div>
        </div>
    );


}
